	public static int areaTriangolo(int base, int altezza) {
		return (base*altezza)/2;
	}
	public static int areaQuadrato(int lato) {
		return (lato*2);
	}
	public static int areaRombo(int dMaggiore, int dMinore) {
		return (dMaggiore*dMinore)/2;
	}
	
